mapper.select:
SELECT TC.CAR_KEY , TC.CAR_NUMBER , TC.CAR_NAME , TC.LENT_CAR_YN , COUNT(TCI.CONTRACT_INFO_KEY) AS CONTRACT_COUNT , MBER.ESNTL_ID , MBER.MBER_FLAG FROM COMTNGNRLMBER MBER , TB_CAR TC , TB_CONTRACT_INFO TCI , ( SELECT CONTRACT_INFO_KEY FROM TB_QUARTER_PAYMENT WHERE ((ASSIGN_STATUS = '01' AND USE_END_DATE >= SYSDATE) OR ASSIGN_STATUS IN ('02', '04')) ) TQP WHERE MBER.ESNTL_ID = #{esntlId} AND MBER.ESNTL_ID = TC.ESNTL_ID AND TC.CAR_KEY = TCI.CAR_KEY(+)
AND TCI.PARKING_PRODUCT(+) = #{searchParkingProduct}
AND TCI.CONTRACT_INFO_KEY = TQP.CONTRACT_INFO_KEY(+) GROUP BY TC.CAR_KEY, TC.CAR_NUMBER, TC.CAR_NAME, TC.LENT_CAR_YN, TCI.CONTRACT_INFO_KEY, MBER.ESNTL_ID, MBER.MBER_FLAG ORDER BY CAR_NUMBER

SELECT PARK_SECTION_KEY AS OPTION_VALUE , PARK_SECTION_NAME AS OPTION_TEXT FROM TB_PARK_SECTION WHERE 1 = 1 AND SECTION_TYPE = '02' AND SECTION_STATUS = '01'
AND MANAGE_ZONE = #{searchManageZone}
ORDER BY PARK_SECTION_NAME ASC

SELECT TPS.PARK_SECTION_KEY , TPS.PARK_SECTION_NAME , TPS.SECTION_TYPE , FN_CODENM('DFM025', SECTION_TYPE) SECTION_TYPE_NAME --구간종류 , TPS.MANAGE_ZONE , FN_CODENM('DFM013', MANAGE_ZONE) MANAGE_ZONE_NAME --관리지구 , TPS.SECTION_CLASSIFY , FN_CODENM('DFM006', SECTION_CLASSIFY) SECTION_CLASSIFY_NAME --구간구분 , TPS.SECTION_TRAIT , FN_CODENM('DFM007', SECTION_TRAIT) SECTION_TRAIT_NAME --구간특성 , TPS.ASSIGN_UNIT , FN_CODENM('DFM005', ASSIGN_UNIT) ASSIGN_UNIT_NAME --구간속성 , TPS.SECTION_STATUS , TPS.VISIT_PARK_YN --방문주차여부 , TPS.TIME_PARK_YN --시간주차여부 , TPS.TOTAL_DIVISION --총구획 , TPS.COMPACT_DIVISION --경차전용구획 , TPS.FEMALE_DIVISION --여성전용구획 , TPS.HANDICAP_DIVISION --장애인전용구획 , TPS.TIME_PARK_CHARGE , TRIM(TO_CHAR(TIME_PARK_CHARGE, '999,999,999,999,999')) TIME_PARK_CHARGE_COMMA --분단위이용요금 , TPS.ALL_DAY_CHARGE , TRIM(TO_CHAR(ALL_DAY_CHARGE, '999,999,999,999,999')) ALL_DAY_CHARGE_COMMA --전일이용요금 , TPS.DAY_TIME_CHARGE , TRIM(TO_CHAR(DAY_TIME_CHARGE, '999,999,999,999,999')) DAY_TIME_CHARGE_COMMA --주간이용요금 , TPS.NIGHT_TIME_CHARGE , TRIM(TO_CHAR(NIGHT_TIME_CHARGE, '999,999,999,999,999')) NIGHT_TIME_CHARGE_COMMA --야간이용요금 , TPS.TIME_PARK_INTERVAL --시간주차간격 , TPS.OPERATION_START_TIME , REGEXP_REPLACE(OPERATION_START_TIME,'(^.{2})','\1:') OPERATION_START_TIME_STR --운영시작시간 , TPS.OPERATION_END_TIME , REGEXP_REPLACE(OPERATION_END_TIME,'(^.{2})','\1:') OPERATION_END_TIME_STR --운영종료시간 , TPS.VISIT_PARK_CHARGE , TRIM(TO_CHAR(VISIT_PARK_CHARGE, '999,999,999,999,999')) VISIT_PARK_CHARGE_COMMA --방문주차요금 , TPS.SECTION_LON , TPS.SECTION_LAT , TPS.MAP_PANO_ID AS SECTION_MAP_PANO_ID , TPS.MAP_PAN AS SECTION_MAP_PAN , TPS.MAP_TILT AS SECTION_MAP_TILT , TPS.MAP_ZOOM AS SECTION_MAP_ZOOM , TPB.PARK_BLOCK_KEY , TPB.PARK_BLOCK_NAME , TPB.BLOCK_STATUS , TPD.PARK_DIVISION_KEY , TPD.DIVISION_NAME , TPD.DIVISION_STATUS , TPD.DIVISION_LON , TPD.DIVISION_LAT , TPD.MAP_PANO_ID AS DIVISION_MAP_PANO_ID , TPD.MAP_PAN AS DIVISION_MAP_PAN , TPD.MAP_TILT AS DIVISION_MAP_TILT , TPD.MAP_ZOOM AS DIVISION_MAP_ZOOM FROM TB_PARK_SECTION TPS , TB_PARK_BLOCK TPB , TB_PARK_DIVISION TPD WHERE TPS.PARK_SECTION_KEY = TPB.PARK_SECTION_KEY(+) AND TPB.PARK_BLOCK_KEY = TPD.PARK_BLOCK_KEY(+) AND TPS.SECTION_STATUS = '01' AND TPB.BLOCK_STATUS(+) = '01' AND TPD.DIVISION_STATUS(+) = '01'
AND TPS.SECTION_TYPE = #{searchSectionType}
AND TPS.MANAGE_ZONE = #{searchManageZone}
AND TPS.PARK_SECTION_KEY = #{searchParkSectionKey}
AND TPB.PARK_BLOCK_KEY = #{searchParkBlockKey}
AND TPD.PARK_DIVISION_KEY = #{searchParkDivisionKey}
