mapper.select:
    	SELECT * 
		FROM   REPLY 
		WHERE  1 = 1 
		       AND DONATE_IDX = #{idx} 
		ORDER  BY REPLY_REGDATE DESC 
		LIMIT  #{beginArticleNum}, #{articleCount}

SELECT D.*, (SELECT Count(*) FROM reply WHERE donate_idx = D.donate_idx) AS REPLYCNT, R2.* FROM donate D LEFT OUTER JOIN (SELECT DISTINCT donate_idx AS DIDX, Max(reply_idx) AS REIDX FROM reply GROUP BY didx) R1 ON R1.didx = D.donate_idx LEFT OUTER JOIN reply R2 ON R2.reply_idx = R1.reidx WHERE 1 = 1
AND D.DONATE_NAME LIKE CONCAT('%',#{donateName},'%')
AND DATE(D.DONATE_DATE) >= DATE(#{startDate})
AND DATE(D.DONATE_DATE) <= DATE(#{endDate})
AND SUBSTRING(D.DONATE_DATE, 1, 4) = #{searchYear}
ORDER BY D.DONATE_DATE DESC, D.DONATE_IDX DESC LIMIT #{beginArticleNum}, #{articleCount}

SELECT ST.STORY_IDX, ST.STORY_AREA, ST.STORY_GUBUN, ST.STORY_TITLE, ST.STORY_ANONYMITY, STORY_HITS, ST.STORY_CONTENTS, ST.STORY_PASSWORD, ST.STORY_WRITER, ST.STORY_IMG, ST.STORY_ORG_IMG, ST.STORY_REGDATE, ST.DONATE_IDX, DN.DONATE_NAME, DN.DONATE_ANONYMITY, (SELECT COUNT(*) FROM STORY_COMMENT WHERE COM_DIV = ST.STORY_GUBUN AND BOD_IDX = ST.STORY_IDX) AS COMCNT FROM STORY ST LEFT OUTER JOIN DONATE DN ON DN.DONATE_IDX = ST.DONATE_IDX WHERE 1 = 1 AND ST.STORY_GUBUN = #{gubun}
AND ST.STORY_TITLE LIKE CONCAT('%',#{searchValue},'%')
AND ST.STORY_CONTENTS LIKE CONCAT('%',#{searchValue},'%')
AND (ST.STORY_TITLE LIKE CONCAT('%',#{searchValue},'%') OR ST.STORY_CONTENTS LIKE CONCAT('%',#{searchValue},'%'))
AND ST.DONATE_IDX = #{donateIdx}
ORDER BY ST.STORY_REGDATE DESC LIMIT #{beginArticleNum}, #{articleCount}

SELECT COUNT(*) AS CNT FROM STORY ST WHERE 1 = 1 AND ST.STORY_GUBUN = #{gubun}
AND ST.STORY_TITLE LIKE CONCAT('%',#{searchValue},'%')
AND ST.STORY_CONTENTS LIKE CONCAT('%',#{searchValue},'%')
AND (ST.STORY_TITLE LIKE CONCAT('%',#{searchValue},'%') OR ST.STORY_CONTENTS LIKE CONCAT('%',#{searchValue},'%'))
AND ST.DONATE_IDX = #{donateIdx}

	SELECT COUNT(*)
      FROM STORY_COMMENT
      WHERE 1=1 
        AND COM_DIV = #{gubun}
        AND BOD_IDX = #{idx}

mapper.delete:
DELETE FROM STORY 
WHERE  1 = 1 
  AND STORY_IDX = #{idx} 
  AND STORY_GUBUN = #{gubun}

mapper.update:
UPDATE STORY 
SET    STORY_AREA = #{storyArea}, 
       STORY_TITLE = #{storyTitle}, 
       STORY_WRITER = #{storyWriter}, 
       STORY_ANONYMITY = #{storyAnonymity}, 
       STORY_CONTENTS = #{storyContents}, 
       DONATE_IDX = #{donateIdx} 
WHERE  1 = 1 
       AND STORY_IDX = #{storyIdx} 
       AND STORY_GUBUN = #{storyGubun}

mapper.select:
SELECT COUNT(*) AS CNT 


		FROM STORY_COMMENT
		WHERE 1=1 AND COM_DIV = #{gubun} AND COM_IDX = #{comIdx} AND COM_PWD = #{pwd}


		FROM STORY
		WHERE 1=1 AND STORY_GUBUN = #{gubun} AND STORY_IDX = #{idx} AND STORY_PASSWORD = #{pwd}
